media_type.script:
try {
   zbx_debug_level = 3;
   Zabbix.Log(zbx_debug_level, 'Webhook request value: ' + value);
   params = JSON.parse(value),
   req = new CurlHttpRequest(),
   fields = {};
   var result = {'chat_id': params.chat_id}; 
   req.AddHeader('Content-Type: application/json');
   if (params.HTTPProxy) {
     req.SetProxy(params.HTTPProxy);
     Zabbix.Log(zbx_debug_level, 'Webhook via proxy: ' + params.HTTPProxy);
   }
   // Docs: https://tlgrm.ru/docs/bots/api#sendmessage
   fields.chat_id = params.chat_id;
   fields.parse_mode = params.parse_mode; //markdown or html
   fields.disable_web_page_preview = params.disable_web_page_preview; //true or false
   params.message = params.message.replace(/\\U\+([\d\w]{4,5})/gi, function(match, grp) {
        return String.fromCharCode(parseInt(grp, 16));
   });
   fields.text = params.subj + '\r\n' + params.message;
   raw_response = req.Post('https://api.telegram.org/bot' + params.token + '/sendMessage',
       JSON.stringify(fields, ['chat_id', 'parse_mode', 'disable_web_page_preview', 'text'])
   );
   if (req.Status() > 0) {
      json_response = JSON.parse(raw_response);
      if (!json_response.ok) {
         result.error_code = json_response.error_code;
         result.error_description = json_response.description;
         throw 'Response code: ' + json_response.error_code + ', Reason: ' + json_response.description;
      }
      result.message_id = json_response.result.message_id;
   } else {
      result.error_code = req.Status();
      throw 'Response code: ' + req.Status();
   }
   Zabbix.Log(zbx_debug_level, 'Response code: ' + req.Status());
} catch (error) {
   Zabbix.Log(zbx_debug_level, 'Telegram failed json: ' + JSON.stringify(fields, ['chat_id', 'parse_mode', 'disable_web_page_preview', 'text']));
   Zabbix.Log(zbx_debug_level, 'Telegram failed: ' + error);
   result = {};
}
//Zabbix.Log(zbx_debug_level, 'Telegram response: ' + JSON.stringify(result));
return JSON.stringify(result);
